import React, { useState, useEffect } from "react";
import { yahooFinApi } from "../API/yahooFinApi";
import { useToasts } from "react-toast-notifications";

const Navbar = ({
  setQuoteSummary,
  fetchChartData,
  searchTerm,
  setSearchTerm,
}) => {
  const { addToast } = useToasts();

  const onHandleSubmit = (ev) => {
    ev.preventDefault();
    yahooFinApi
      .get(
        `/v11/finance/quoteSummary/${searchTerm.toLowerCase()}.BK?lang=en&region=US&modules=defaultKeyStatistics%2CassetProfile%2CfinancialData%2CquoteType%2CsummaryDetail%2CbalanceSheetHistory%2CincomeStatementHistory`
      )
      .then((res) => {
        if (res.data.quoteSummary.result) {
          setQuoteSummary(res.data.quoteSummary.result[0]);
        } else {
          addToast(res.data.quoteSummary.error.code, {
            appearance: "error",
            autoDismiss: true,
          });
        }
      })
      .catch((err) => {
        console.log("err", err.message);
      });

    // fetchChartData();
  };

  return (
    <div>
      <nav className="navbar navbar-light bg-light border">
        <div className="container-fluid">
          <form className="d-flex align-items-center" onSubmit={onHandleSubmit}>
            <div className="text-primary fs-4">VALUATED</div>
            <input
              className="form-control mx-4"
              type="search"
              placeholder="ค้นหารายชื่อหุ้น"
              onChange={(ev) => setSearchTerm(ev.target.value)}
              value={searchTerm}
            />
          </form>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
