import React, { useState, useEffect } from "react";
import { yahooFinApi } from "../API/yahooFinApi";
import { useToasts } from "react-toast-notifications";
import Modal from "react-modal";

const Navbar = ({
  setQuoteSummary,
  fetchChartData,
  searchTerm,
  setSearchTerm,
}) => {
  const { addToast } = useToasts();
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const customStyles = {
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      width: "500px",
      transform: "translate(-50%, -50%)",
    },
  };

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const onHandleSubmit = (ev) => {
    ev.preventDefault();
    yahooFinApi
      .get(
        `/v11/finance/quoteSummary/${searchTerm.toLowerCase()}.BK?lang=en&region=US&modules=defaultKeyStatistics%2CassetProfile%2CfinancialData%2CquoteType%2CsummaryDetail%2CbalanceSheetHistory%2CincomeStatementHistory`
      )
      .then((res) => {
        if (res.data.quoteSummary.result) {
          setQuoteSummary(res.data.quoteSummary.result[0]);
        } else {
          addToast(res.data.quoteSummary.error.code, {
            appearance: "error",
            autoDismiss: true,
          });
        }
      })
      .catch((err) => {
        console.log("err", err.message);
      });
  };

  return (
    <div>
      <nav className="navbar navbar-light bg-light border">
        <div className="container-fluid">
          <div className="d-flex align-items-center">
            <div className="text-primary fs-4">VALUATED</div>
            <input
              className="form-control mx-4"
              type="search"
              placeholder="ค้นหารายชื่อหุ้น"
              onClick={openModal}
              // onChange={(ev) => setSearchTerm(ev.target.value)}
              // value={searchTerm}
            />
            <Modal
              isOpen={modalIsOpen}
              onRequestClose={closeModal}
              style={customStyles}
            >
              <input
                className="form-control"
                type="search"
                placeholder="ค้นหารายชื่อหุ้น"
                onChange={(ev) => setSearchTerm(ev.target.value)}
                value={searchTerm}
              />
            </Modal>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
