import React, { useEffect } from "react";
import scrapeIt from "scrape-it";

const News = () => {
  useEffect(() => {
    scrapeIt("https://stock.gapfocus.com/detail/STGT", {
      title: "h3",
      // posts: {
      //   listItem: "div.talk-row",
      //   data: {
      //     title: "sub.talk-detail",
      //     url: {
      //       selector: "a",
      //       attr: "href",
      //     },
      //   },
      // },
    })
      .then(({ data, response }) => {
        console.log(`Status Code: ${response.statusCode}`);
        console.log(data);
      })
      .catch((err) => {
        console.log("err", err.message);
      });
  }, []);

  return <div></div>;
};

export default News;
