import React, { useEffect } from "react";
import scrapeIt from "scrape-it";

const News = () => {
  useEffect(() => {
    scrapeIt("https://stock.gapfocus.com/detail/STGT", {
      posts: {
        listItem: "div.talk-row",
        // name: "pages",
        data: {
          title: "h6",
          url: {
            selector: "a",
            attr: "href",
          },
          avatar: {
            selector: ".card img",
            attr: "src",
          },
        },
      },
    }).then(({ data, response }) => {
      console.log(`Status Code: ${response.statusCode}`);
      console.log(data);
    });
  }, []);

  return <div></div>;
};

export default News;
