import React from "react";

const Table = ({ quoteSummary }) => {
  const formatNumber = (data) => {
    return Math.round((data / 1000000) * 100) / 100;
  };

  const displayTable = (
    <>
      <table className="table">
        <thead>
          <tr>
            <td></td>
            {quoteSummary
              ? quoteSummary?.quoteSummary.result[0].incomeStatementHistory.incomeStatementHistory.map(
                  (item) => {
                    return (
                      <td>{new Date(item.endDate.raw * 1000).getFullYear()}</td>
                    );
                  }
                )
              : "-"}
          </tr>
        </thead>
        <tbody>
          <td>
            <tr>รายได้ (ลบ.)</tr>
            <tr>การเติบโตของรายได้</tr>
            <tr>อัตรากำไรสุทธิ</tr>
            <tr>กำไรสุทธิ (ลบ.)</tr>
            <tr>จำนวนหุ้นที่จดทะเบียน (ล้านหุ้น)</tr>
          </td>
          {quoteSummary
            ? quoteSummary?.quoteSummary.result[0].incomeStatementHistory.incomeStatementHistory.map(
                (item, index) => {
                  let revGrowth;
                  if (
                    index + 1 <
                    quoteSummary?.quoteSummary.result[0].incomeStatementHistory
                      .incomeStatementHistory.length
                  ) {
                    revGrowth = `
                      ${
                        Math.round(
                          (item.totalRevenue.raw /
                            quoteSummary?.quoteSummary.result[0]
                              .incomeStatementHistory.incomeStatementHistory[
                              index + 1
                            ].totalRevenue.raw -
                            1) *
                            100 *
                            100
                        ) / 100
                      } %`;
                  } else {
                    revGrowth = "-";
                  }

                  return (
                    <td>
                      <tr>{formatNumber(item.totalRevenue.raw)}</tr>
                      <tr>{revGrowth} </tr>
                      <tr>
                        {Math.round(
                          (item.netIncome.raw / item.totalRevenue.raw) *
                            100 *
                            100
                        ) / 100}{" "}
                        %
                      </tr>
                      <tr>{formatNumber(item.netIncome.raw)}</tr>
                      <tr>
                        {formatNumber(
                          quoteSummary?.quoteSummary.result[0]
                            .balanceSheetHistory.balanceSheetStatements[index]
                            .totalStockholderEquity.raw
                        )}
                      </tr>
                    </td>
                  );
                }
              )
            : "-"}
        </tbody>
      </table>
    </>
  );

  return <div>{quoteSummary ? displayTable : ""}</div>;
};

export default Table;

