import React from "react";

const Table = ({ quoteSummary }) => {
  const formatNumber = (data) => {
    return Math.round((data / 1000000) * 100) / 100;
  };

  const displayTable = (
    <>
      <table className="table">
        <thead>
          <tr>
            <td></td>
            {quoteSummary
              ? quoteSummary?.quoteSummary.result[0].incomeStatementHistory.incomeStatementHistory.map(
                  (item) => {
                    return (
                      <td>{new Date(item.endDate.raw * 1000).getFullYear()}</td>
                    );
                  }
                )
              : "-"}
          </tr>
        </thead>
        <tbody>
          <td>
            <tr>รายได้ (ลบ.)</tr>
            <tr>การเติบโตของรายได้</tr>
            <tr>อัตรากำไรสุทธิ</tr>
            <tr>กำไรสุทธิ (ลบ.)</tr>
            <tr>จำนวนหุ้นที่จดทะเบียน (ล้านหุ้น)</tr>
          </td>
          {quoteSummary
            ? quoteSummary?.quoteSummary.result[0].incomeStatementHistory.incomeStatementHistory.map(
                (item) => {
                  return (
                    <td>
                      <tr>{formatNumber(item.totalRevenue.raw)}</tr>
                      <tr>%</tr>
                      <tr>
                        {Math.round(
                          (item.netIncome.raw / item.totalRevenue.raw) *
                            100 *
                            100
                        ) / 100}{" "}
                        %
                      </tr>
                      <tr>{formatNumber(item.netIncome.raw)}</tr>
                    </td>
                  );
                }
              )
            : "-"}
        </tbody>
      </table>
    </>
  );

  return <div>{displayTable}</div>;
};

export default Table;

