import React, { useState, useEffect } from "react";
import { yahooFinApi } from "../API/yahooFinApi";
import { useToasts } from "react-toast-notifications";
import Modal from "react-modal";
import { FaTimesCircle } from "react-icons/fa";
import { stockList } from "../assets/StockList";

const Navbar = ({
  setQuoteSummary,
  fetchChartData,
  searchTerm,
  setSearchTerm,
}) => {
  const { addToast } = useToasts();
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const customStyles = {
    content: {
      top: "20%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      width: "500px",
      transform: "translate(-50%, -50%)",
    },
  };

  const styles = {
    textBrand: {
      color: "rgba(21, 101, 192, 1)",
      fontWeight: "600",
      fontSize: "32px",
    },
  };

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setSearchTerm("");
  };

  const onHandleSubmit = (ev) => {
    ev.preventDefault();
    yahooFinApi
      .get(
        `/v11/finance/quoteSummary/${searchTerm.toLowerCase()}.BK?lang=en&region=US&modules=defaultKeyStatistics%2CassetProfile%2CfinancialData%2CquoteType%2CsummaryDetail%2CbalanceSheetHistory%2CincomeStatementHistory`
      )
      .then((res) => {
        if (res.data.quoteSummary.result) {
          setQuoteSummary(res.data.quoteSummary.result[0]);
        } else {
          addToast(res.data.quoteSummary.error.code, {
            appearance: "error",
            autoDismiss: true,
          });
        }
      })
      .catch((err) => {
        console.log("err", err.message);
      })
      .finally(() => {
        closeModal();
      });
  };

  return (
    <div>
      <nav className="navbar navbar-light bg-light border">
        <div className="container-fluid">
          <div className="d-flex align-items-center">
            <div style={styles.textBrand}>VALUATE</div>
            <input
              className="form-control mx-4"
              placeholder="ค้นหารายชื่อหุ้น"
              onClick={openModal}
            />
            <div className="position-relative">
              <span style={{ position: "absolute", letft: "14px" }}>lb</span>
              <input />
            </div>

            <Modal
              isOpen={modalIsOpen}
              onRequestClose={closeModal}
              style={customStyles}
            >
              <form onSubmit={onHandleSubmit}>
                <div className="d-flex justify-content-between align-items-center  mb-2">
                  <div className="fw-bold">ค้นหาข้อมูลหุ้น</div>
                  <div style={{ cursor: "pointer" }} onClick={closeModal}>
                    <FaTimesCircle />
                  </div>
                </div>
                <div class="input-group ">
                  <input
                    className="form-control"
                    type="search"
                    placeholder="ค้นหา"
                    onChange={(ev) => setSearchTerm(ev.target.value)}
                    value={searchTerm}
                    list="stock-list"
                  />
                  <datalist id="stock-list">
                    {stockList.map((item) => {
                      return (
                        <option value={item.Symbol}>{item.Company}</option>
                      );
                    })}
                  </datalist>

                  <button class="btn btn-primary" type="submit">
                    ค้นหา
                  </button>
                </div>
              </form>
            </Modal>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
