import React, { useState } from "react";

const Table = ({ quoteSummary }) => {
  const [growth, setGrowth] = useState(0);
  const [npm, setNpm] = useState(0);
  const [totalShares, setTotalShares] = useState(0);
  const revenue =
    quoteSummary?.incomeStatementHistory?.incomeStatementHistory[0]
      ?.totalRevenue.raw +
    quoteSummary?.incomeStatementHistory?.incomeStatementHistory[0]
      ?.totalRevenue.raw *
      (growth / 100);

  const formatNumber = (data) => {
    return (Math.round((data / 1000000) * 100) / 100)
      .toString()
      .replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  };

  const displayTable = (
    <>
      <table className="table ">
        <thead>
          <tr>
            <td></td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item) => {
                    return (
                      <td key={item.endDate.raw}>
                        {new Date(item.endDate.raw * 1000).getFullYear()}
                      </td>
                    );
                  }
                )
              : "-"}
          </tr>
        </thead>
        <tbody>
          <td>
            <tr>รายได้ (ลบ.)</tr>
            <tr>การเติบโตของรายได้</tr>
            <tr>อัตรากำไรสุทธิ</tr>
            <tr>กำไรสุทธิ (ลบ.)</tr>
            <tr>จำนวนหุ้นที่จดทะเบียน (ล้านหุ้น)</tr>
          </td>
          {quoteSummary
            ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                (item, index) => {
                  let revGrowth;
                  if (
                    index + 1 <
                    quoteSummary?.incomeStatementHistory.incomeStatementHistory
                      .length
                  ) {
                    revGrowth = `
                      ${
                        Math.round(
                          (item.totalRevenue.raw /
                            quoteSummary?.incomeStatementHistory
                              .incomeStatementHistory[index + 1].totalRevenue
                              .raw -
                            1) *
                            100 *
                            100
                        ) / 100
                      } %`;
                  } else {
                    revGrowth = "-";
                  }

                  return (
                    <td key={item.endDate.raw}>
                      <tr>{formatNumber(item.totalRevenue.raw)}</tr>
                      <tr>{revGrowth} </tr>
                      <tr>
                        {Math.round(
                          (item.netIncome.raw / item.totalRevenue.raw) *
                            100 *
                            100
                        ) / 100}{" "}
                        %
                      </tr>
                      <tr>{formatNumber(item.netIncome.raw)}</tr>
                      <tr>
                        {formatNumber(
                          quoteSummary?.balanceSheetHistory
                            .balanceSheetStatements[index].commonStock.raw
                        )}
                      </tr>
                    </td>
                  );
                }
              )
            : "-"}
        </tbody>
      </table>
    </>
  );

  const displayTable2 = (
    <>
      <table className="table ">
        <thead className="text-center">
          <tr>
            <td></td>
            <td>{new Date().getFullYear()}</td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item) => {
                    return (
                      <td key={item.endDate.raw}>
                        {new Date(item.endDate.raw * 1000).getFullYear()}
                      </td>
                    );
                  }
                )
              : "-"}
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>รายได้ (ลบ.)</td>
            <td className="text-center">{formatNumber(revenue)}</td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item) => {
                    return (
                      <td className="text-center">
                        {" "}
                        {formatNumber(item.totalRevenue.raw)}
                      </td>
                    );
                  }
                )
              : ""}
          </tr>
          <tr>
            <td>การเติบโตของรายได้ (%)</td>
            <td className="text-center">
              <input
                style={{ width: "100px" }}
                type="number"
                value={growth}
                onChange={(ev) => setGrowth(ev.target.value)}
                className="text-center"
              />
            </td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item, index) => {
                    let revGrowth;
                    if (
                      index + 1 <
                      quoteSummary?.incomeStatementHistory
                        .incomeStatementHistory.length
                    ) {
                      revGrowth = `
                        ${
                          Math.round(
                            (item.totalRevenue.raw /
                              quoteSummary?.incomeStatementHistory
                                .incomeStatementHistory[index + 1].totalRevenue
                                .raw -
                              1) *
                              100 *
                              100
                          ) / 100
                        } %`;
                    } else {
                      revGrowth = "-";
                    }

                    return <td className="text-center"> {revGrowth}</td>;
                  }
                )
              : ""}
          </tr>

          <tr>
            <td>อัตรากำไรสุทธิ (%)</td>
            <td className="text-center">
              <input
                style={{ width: "100px" }}
                type="number"
                value={npm}
                onChange={(ev) => setNpm(ev.target.value)}
                className="text-center"
              />
            </td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item) => {
                    return (
                      <td className="text-center">
                        {Math.round(
                          (item.netIncome.raw / item.totalRevenue.raw) *
                            100 *
                            100
                        ) / 100}
                        %
                      </td>
                    );
                  }
                )
              : ""}
          </tr>
          <tr>
            <td>กำไรสุทธิ (ลบ.)</td>
            <td className="text-center">
              {revenue && npm ? formatNumber(revenue * (npm / 100)) : ""}
            </td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item) => {
                    return (
                      <td className="text-center">
                        {formatNumber(item.netIncome.raw)}
                      </td>
                    );
                  }
                )
              : ""}
          </tr>
          <tr>
            <td>จำนวนหุ้นที่จดทะเบียน (ล้านหุ้น)</td>
            <td className="text-center">
              <input
                style={{ width: "100px" }}
                type="number"
                value={totalShares}
                onChange={(ev) => setTotalShares(ev.target.value)}
                className="text-center"
              />
            </td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item, index) => {
                    return (
                      <td className="text-center">
                        {formatNumber(
                          quoteSummary?.balanceSheetHistory
                            .balanceSheetStatements[index].commonStock.raw
                        )}
                      </td>
                    );
                  }
                )
              : ""}
          </tr>
          <tr>
            <td>กำไรต่อหุ้น (บาท)</td>
            <td className="text-center">
              {revenue && totalShares
                ? (revenue * (npm / 100)) / totalShares
                : ""}
            </td>
            {quoteSummary
              ? quoteSummary?.incomeStatementHistory.incomeStatementHistory.map(
                  (item, index) => {
                    return (
                      <td className="text-center">
                        {formatNumber(
                          +item.netIncome.raw /
                            +quoteSummary?.balanceSheetHistory
                              .balanceSheetStatements[index].commonStock.raw
                        )}
                      </td>
                    );
                  }
                )
              : ""}
          </tr>
        </tbody>
      </table>
    </>
  );

  return <div>{quoteSummary ? displayTable2 : ""}</div>;
};

export default Table;
