document.body.style.position = "relative";

var container = document.createElement("div");
document.body.appendChild(container);

var width = 600;
var height = 300;

var data = [
  {
    time: "2019-05-24",
    value: 84.0,
  },
  {
    time: "2019-05-27",
    value: 83.8,
  },
  {
    time: "2019-05-28",
    value: 84.32,
  },
  {
    time: "2019-05-29",
    value: 83.88,
  },
  {
    time: "2019-05-30",
    value: 84.58,
  },
  {
    time: "2019-05-31",
    value: 81.2,
  },
  {
    time: "2019-06-03",
    value: 84.35,
  },
  {
    time: "2019-06-04",
    value: 85.66,
  },
  {
    time: "2019-06-05",
    value: 86.51,
  },
];

var chart = (window.tvchart = LightweightCharts.createChart(container, {
  width: width,
  height: height,
  rightPriceScale: {
    scaleMargins: {
      top: 0.35,
      bottom: 0.2,
    },
    borderVisible: false,
  },
  timeScale: {
    borderVisible: false,
  },
  grid: {
    horzLines: {
      color: "#eee",
      visible: false,
    },
    vertLines: {
      color: "#ffffff",
    },
  },
  crosshair: {
    horzLine: {
      visible: false,
      labelVisible: false,
    },
    vertLine: {
      visible: true,
      style: 0,
      width: 2,
      color: "rgba(32, 38, 46, 0.1)",
      labelVisible: false,
    },
  },
}));

var series = chart.addAreaSeries({
  topColor: "rgba(19, 68, 193, 0.4)",
  bottomColor: "rgba(0, 120, 255, 0.0)",
  lineColor: "rgba(19, 40, 153, 1.0)",
  lineWidth: 3,
});

series.setData(data);

function businessDayToString(businessDay) {
  return new Date(
    Date.UTC(businessDay.year, businessDay.month - 1, businessDay.day, 0, 0, 0)
  ).toLocaleDateString();
}

var toolTipMargin = 10;
var priceScaleWidth = 50;
var toolTip = document.createElement("div");
toolTip.className = "three-line-legend";
container.appendChild(toolTip);
toolTip.style.display = "block";
toolTip.style.left = 3 + "px";
toolTip.style.top = 3 + "px";

function setLastBarText() {
  var dateStr =
    data[data.length - 1].time.year +
    " - " +
    data[data.length - 1].time.month +
    " - " +
    data[data.length - 1].time.day;
  toolTip.innerHTML =
    '<div style="font-size: 24px; margin: 4px 0px; color: #20262E"> AEROSPACE</div>' +
    '<div style="font-size: 22px; margin: 4px 0px; color: #20262E">' +
    data[data.length - 1].value +
    "</div>" +
    "<div>" +
    dateStr +
    "</div>";
}

setLastBarText();

chart.subscribeCrosshairMove(function (param) {
  if (
    param === undefined ||
    param.time === undefined ||
    param.point.x < 0 ||
    param.point.x > width ||
    param.point.y < 0 ||
    param.point.y > height
  ) {
    setLastBarText();
  } else {
    dateStr =
      param.time.year + " - " + param.time.month + " - " + param.time.day;
    var price = param.seriesPrices.get(series);
    toolTip.innerHTML =
      '<div style="font-size: 24px; margin: 4px 0px; color: #20262E"> AEROSPACE</div>' +
      '<div style="font-size: 22px; margin: 4px 0px; color: #20262E">' +
      (Math.round(price * 100) / 100).toFixed(2) +
      "</div>" +
      "<div>" +
      dateStr +
      "</div>";
  }
});
