import React, { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";

const Chart = ({ quoteSpark, setRangeChart, quoteSummary }) => {
  const [chartData, setChartData] = useState("");

  const convertDate = (value) => {
    const result = new Date(value * 1000);

    let date = result.getDate();
    let month = result.getMonth() + 1;
    let year = result.getFullYear();

    if (date < 10) {
      date = "0" + date;
    }
    if (month < 10) {
      month = "0" + month;
    }

    return `${date}/${month}/${year}`;
  };

  const getStandardDeviation = (dataArray) => {
    const n = dataArray.length;
    const mean = dataArray.reduce((a, b) => a + b) / n;
    return Math.sqrt(
      dataArray.map((x) => Math.pow(x - mean, 2)).reduce((a, b) => a + b) / n
    );
  };

  console.log("quoteSpark", quoteSpark);
  console.log("quoteSummary", quoteSummary);

  const getPrice = quoteSpark?.close?.map((item, index) => {
    return {
      price: item,
      year: new Date(quoteSpark.timestamp[index] * 1000).getFullYear(),
    };
  });

  const getEPS =
    quoteSummary?.quoteSummary?.result[0]?.incomeStatementHistory?.incomeStatementHistory?.map(
      (item, index) => {
        return {
          eps:
            item.netIncome.raw /
            quoteSummary?.quoteSummary.result[0].balanceSheetHistory
              .balanceSheetStatements[index].commonStock.raw,
          year: new Date(item.endDate.raw * 1000).getFullYear(),
        };
      }
    );

  const getPE = getPrice.map((item, index) => {
    const resEps = getEPS.find((data) => data.year === item.year);
    if (resEps) {
      return {
        ...item,
        pe: item.price / resEps,
      };
    } else {
      return item;
    }
  });

  console.log("getPrice", getPrice);
  console.log("getEPS", getEPS);
  console.log("getPE", getPE);

  const chartGenerate = () => {
    const labels = quoteSpark?.timestamp?.map((item) => convertDate(item));

    const datas = {
      labels: labels,
      datasets: [
        {
          label: quoteSpark?.symbol?.replace(".BK", ""),
          backgroundColor: "rgb(255, 99, 132)",
          borderColor: "rgb(255, 99, 132)",
          pointRadius: "0",
          borderWidth: 2,
          data: quoteSpark?.close,
        },
      ],
    };

    setChartData(datas);
  };

  let options = {
    scales: {
      x: {
        ticks: {
          maxTicksLimit: 10,
          maxRotation: 90,
          minRotation: 90,
        },
      },
    },
    plugins: {
      zoom: {
        zoom: {
          wheel: {
            enabled: true,
          },
          pinch: {
            enabled: true,
          },
          mode: "xy",
        },
        pan: {
          enabled: true,
          mode: "xy",
          speed: 1,
          threshold: 1,
        },
      },

      annotation: {
        annotations: {
          line1: {
            type: "line",
            yMin: quoteSpark?.close,
            // yMax: quoteSpark?.close,
            borderColor: "green",
            borderWidth: 2,
          },
          // line2: {
          //   type: "line",
          //   xMin: transections[transections.length - 1].timeStamp,
          //   xMax: transections[transections.length - 1].timeStamp,
          //   borderColor: "rgb(0, 0, 0)",
          //   borderWidth: 2,
          // },
        },
      },
    },
  };

  useEffect(() => {
    if (quoteSpark) chartGenerate();
  }, [quoteSpark]);

  const displayRange = (
    <div className="d-flex justify-content-between align-items-center">
      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-1d"
          value="1d"
        />
        <label className="btn btn-outline-primary" htmlFor="range-1d">
          1 Day
        </label>
      </div>

      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-5d"
          value="5d"
        />
        <label className="btn btn-outline-primary" htmlFor="range-5d">
          5 Day
        </label>
      </div>
      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-1mo"
          value="1mo"
        />
        <label className="btn btn-outline-primary" htmlFor="range-1mo">
          1 Month
        </label>
      </div>
      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-3mo"
          value="3mo"
        />
        <label className="btn btn-outline-primary" htmlFor="range-3mo">
          3 Month
        </label>
      </div>
      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-6mo"
          value="6mo"
        />
        <label className="btn btn-outline-primary" htmlFor="range-6mo">
          6 Month
        </label>
      </div>
      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-1y"
          value="1y"
        />
        <label className="btn btn-outline-primary" htmlFor="range-1y">
          1 Year
        </label>
      </div>
      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-5y"
          value="5y"
          defaultChecked
        />
        <label className="btn btn-outline-primary" htmlFor="range-5y">
          5 Year
        </label>
      </div>
      <div>
        <input
          onChange={(ev) => setRangeChart(ev.target.value)}
          name="range-chart"
          type="radio"
          className="btn-check"
          id="range-max"
          value="max"
        />
        <label className="btn btn-outline-primary" htmlFor="range-max">
          Max
        </label>
      </div>
    </div>
  );

  return (
    <div className="my-4">
      {quoteSpark ? (
        <>
          {displayRange}
          <Line data={chartData} options={options} />
        </>
      ) : (
        ""
      )}
    </div>
  );
};

export default Chart;
