import React, { useState, useEffect } from "react";
import Chart from "kaktana-react-lightweight-charts";

const LightChart = () => {
  const [chartData, setChartData] = useState([]);

  const generateChart = () => {
    setChartData([
      {
        data: [
          { time: "2018-12-01", value: 32.51 },
          { time: "2018-12-02", value: 31.11 },
          { time: "2018-12-03", value: 27.02 },
          { time: "2018-12-04", value: 27.32 },
          { time: "2018-12-05", value: 25.17 },
          { time: "2018-12-06", value: 28.89 },
          { time: "2018-12-07", value: 25.46 },
          { time: "2018-12-08", value: 23.92 },
          { time: "2018-12-09", value: 22.68 },
          { time: "2018-12-10", value: 22.67 },
          { time: "2018-12-11", value: 27.57 },
          { time: "2018-12-12", value: 24.11 },
          { time: "2018-12-13", value: 30.74 },
        ],
      },
    ]);
  };

  const options = {
    alignLabels: true,
    timeScale: {
      rightOffset: 12,
      barSpacing: 3,
      fixLeftEdge: true,
      lockVisibleTimeRangeOnResize: true,
      rightBarStaysOnScroll: true,
      borderVisible: false,
      borderColor: "#fff000",
      visible: true,
      timeVisible: true,
      secondsVisible: false,
    },
  };

  useEffect(() => {
    generateChart();
  }, []);

  return (
    <div>
      <Chart
        options={options}
        areaSeries={chartData}
        lineSeries={chartData}
        autoWidth
        height={320}
      />
    </div>
  );
};

export default LightChart;
