import React, { useState, useLayoutEffect } from "react";
import { yahooFinApi } from "../API/yahooFinApi";
import { useToasts } from "react-toast-notifications";

const Navbar = ({
  setQuoteSummary,
  setQuoteSpark,
  rangeChart,
  quoteSummary,
}) => {
  const { addToast } = useToasts();
  const [searchTerm, setSearchTerm] = useState("");

  const onHandleSearch = (ev) => {
    setSearchTerm(ev.target.value);
  };

  const fetchChartData = () => {
    yahooFinApi
      .get(
        `/v8/finance/spark?interval=1d&range=${rangeChart}&symbols=${searchTerm.toLowerCase()}.BK`
      )
      .then((res) => {
        const firstObj = res.data[Object.keys(res.data)[0]];
        console.log(
          "res.data.quoteSummary.results",
          res.data.quoteSummary.results
        );
        if (res.data.quoteSummary.results) {
          setQuoteSpark(firstObj);
        } else {
          addToast(res.data.quoteSummary.error.code, {
            appearance: "error",
            autoDismiss: true,
          });
        }
      })
      .catch((err) => {
        console.log("err", err.message);
      });
  };

  const onHandleSubmit = (ev) => {
    ev.preventDefault();
    yahooFinApi
      .get(
        `/v11/finance/quoteSummary/${searchTerm.toLowerCase()}.BK?lang=en&region=US&modules=defaultKeyStatistics%2CassetProfile%2CfinancialData%2CquoteType%2CsummaryDetail%2CbalanceSheetHistory%2CincomeStatementHistory`
      )
      .then((res) => {
        setQuoteSummary(res.data);
      })
      .catch((err) => {
        console.log("err", err.message);
      });

    fetchChartData();
  };

  useLayoutEffect(() => {
    fetchChartData();
  }, [rangeChart, quoteSummary]);

  return (
    <div>
      <nav className="navbar navbar-light bg-light">
        <div className="container-fluid">
          <form className="d-flex" onSubmit={onHandleSubmit}>
            <input
              className="form-control me-2"
              type="search"
              placeholder="ค้นหารายชื่อหุ้น"
              onChange={onHandleSearch}
              value={searchTerm}
            />
          </form>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
