import React, { useEffect } from "react";
// import { Puppeteer } from "puppeteer";
import puppeteer from "puppeteer";

const News = () => {
  const scrapeProduct = async (url) => {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    await page.goto(url);

    const [el] = await page.$x('//*[@id="pantip:home:home:1003"]/div[1]');
    const txt = await el.getProperty("text");
    const rawText = await txt.jsonValue();

    console.log({ rawText });
    browser.close();
  };

  useEffect(() => {
    scrapeProduct("https://pantip.com/");
  }, []);

  return <div></div>;
};

export default News;
